        function run(rogerthat,messageFlowRun){
            var d = new Date();
            var now = parseInt(d.getTime() / 1000 - d.getTimezoneOffset() * 60);
            var sandwichSettings = rogerthat.service.data.sandwich_settings;
            var hasLeapTime = sandwichSettings.leap_time_enabled;
            if(!hasLeapTime){
                return {
                    outlet: 'message_remark'
                };
            }
            var minDate = now + sandwichSettings.leap_time;
            var maxDate = now + 86400;
            var result = {
                outlet: 'message_takeaway_time',
                form: {
                    widget: {
                        has_max_date: true,
                        has_min_date: true,
                        min_date: minDate,
                        max_date: maxDate,
                        date: minDate + 3600
                    }
                }
            };
            return result;
        }







        function run(rogerthat, messageFlowRun){
            "use strict";
            var typesCount = {{ types|length }};
            var out;

            if (typesCount <= 1) {
                out = 'skip_types';
            } else {
                out = 'show_types';
            }

            return {
                outlet: out,
                defaultValue: messageFlowRun.flow_params.sandwichType
            };
        }







        function run(rogerthat, messageFlowRun){
            "use strict";
            var toppingsCount = {{ toppings|length }};
            var out;

            if (toppingsCount <= 1) {
                out = 'skip_toppings';
            } else {
                out = 'show_toppings';
            }

            return {
                outlet: out,
                defaultValue: messageFlowRun.flow_params.sandwichTopping
            };
        }




    {% translate language, 'common', 'error-occured-unknown-try-again' %}










        // Mapping from datastore ids to cost in cents
        var types = JSON.parse('{{ types_json | safe }}');
        var toppings = JSON.parse('{{ toppings_json | safe }}');
        var options = JSON.parse('{{ options_json | safe }}');

        var hasType = false;
        function getAmount(split) {
            var stepId = split[0], id = split[1];
            switch (stepId) {
                case 'type':
                    hasType = true;
                    return types[id];
                case 'topping':
                    return toppings[id];
                case 'option':
                    return options[id];
                default:
                    return 0;
            }
        }

        function run(rogerthat, messageFlowRun) {
            var amount = 0;
            var sandwichesCount = 0;
            for (var _i = 0, steps_1 = messageFlowRun.steps; _i < steps_1.length; _i++) {
                var step = steps_1[_i];
                if (step.form_result && step.form_result.result) {
                    if (step.form_result.result.values) {
                        for (var _a = 0, _b = step.form_result.result.values; _a < _b.length; _a++) {
                            var val = _b[_a];
                            amount += getAmount(val.split('_'));
                        }
                    } else{
                        var split = step.form_result.result.value.split('_');
                        // Topping is mandatory, use that to count amount of sandwiches
                        if (split[0] === 'topping') {
                            sandwichesCount++;
                        }
                        amount += getAmount(split);
                    }
                }
            }
            if (!hasType) {
                var firstType = Object.keys(types)[0];
                amount += types[firstType] * sandwichesCount;
            }
            if (amount <= 0) {
                return {outlet: 'results_flush'};
            }

            var amountStr = (amount / 100).toFixed(2);
            var outlet = 'message_pay_required';
            if (rogerthat.service.data.settings.payment.optional) {
                outlet = 'message_pay_optional';
            }
            var methods = [];
            var providers = rogerthat.service.data.settings.payment.providers;
            for (var i = 0; i < providers.length; i++) {
                var provider = providers[i];
                switch (provider.provider_id) {
                    case 'payconiq':
                        methods.push({
                            provider_id: 'payconiq',
                            currency: 'EUR',
                            amount: 0,
                            target: rogerthat.service.account,
                            precision: 2,
                            calculate_amount: true,
                        });
                        break;
                    case 'threefold':
                        if (provider.settings.address) {
                            methods.push({
                                provider_id: 'threefold',
                                currency: 'TFT',
                                amount: 0,
                                target: provider.settings.address,
                                precision: 5,
                                calculate_amount: true,
                            });
                        }
                        break;
                }
            }
            return {
                outlet: outlet,
                message: "{% translate language, 'common', 'pay_widget_message', '_duplicate_backslashes=True' %}".replace('%(amount)s', amountStr).replace('%(currency)s', 'EUR'),
                defaultValue: {
                    memo: "{% translate language, 'common', 'pay_widget_memo', '_duplicate_backslashes=True' %}".replace('%(name)s', rogerthat.service.name).replace('%(app_name)s', rogerthat.system.appName),
                    target: rogerthat.service.account,
                    base_method:{
                        currency: '{{ settings.currency }}',
                        amount: amount,
                        precision: 2,
                    },
                    test_mode: rogerthat.service.data.settings.payment.test_mode,
                    methods: methods,
                }
            };
        }







