{% translate language, 'common', 'news_target_audience_explanation' %}





                    function run (result, rogerthat) {
                        var apps = result.values;
                        if (apps.length < 1) {
                            return "{% translate language, 'common', 'news_target_audience_select_at_least_one_app' %}";
                        }
                        return true;
                    }








                function run(rogerthat, messageFlowRun) {
                    var newsGroups = rogerthat.service.data.news_groups;
                    var value = newsGroups[0].group_type;
                    var choices = newsGroups.map(function(group){
                        return {value: group.group_type, label: group.name};
                    });
                    var result = {
                        outlet: 'message_label',
                        form: {
                            widget: {
                                value: value,
                                choices: choices
                            }
                        }
                    };
                    return result;
                }






                function run(rogerthat, messageFlowRun) {
                    "use strict";
                    var result;
                    var apps = rogerthat.service.data.broadcast_target_audience;

                    var choices = [], values = [];
                    for (var app_id in apps) {
                        choices.push({
                            value: app_id,
                            label: apps[app_id]
                        });
                        values.push(app_id);
                    }
                    result = {
                        outlet: 'message_target_audience',
                        form: {
                            widget: {
                                values: values,
                                choices: choices
                            }
                        }
                    };
                    return result;
                }
